serMapper&lt;/span&gt; &lt;span class=&#34;variable&#34;&gt;mapper2&lt;/span&gt; &lt;span class=&#34;operator&#34;&gt;=&lt;/span&gt; sqlSession2.getMapper(IUserMapper.class);  &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;type&#34;&gt;User&lt;/span&gt; &lt;span class=&#34;variable&#34;&gt;user&lt;/span&gt; &lt;span class=&#34;operator&#34;&gt;=&lt;/span&gt; mapper.selectByPrimaryKey(&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;);  &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    System.out.println(user);  &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    mapper2.updateUserByPrimaryKey(&lt;span class=&#34;keyword&#34;&gt;new&lt;/span&gt; &lt;span class=&#34;title class_&#34;&gt;User&lt;/span&gt;(&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;, &lt;span class=&#34;string&#34;&gt;&amp;quot;gaga&amp;quot;&lt;/span&gt;));  &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;type&#34;&gt;User&lt;/span&gt; &lt;span class=&#34;variable&#34;&gt;user1&lt;/span&gt; &lt;span class=&#34;operator&#34;&gt;=&lt;/span&gt; mapper.selectByPrimaryKey(&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;);  &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    System.out.println(user1); &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    System.out.println(&lt;span class=&#34;string&#34;&gt;&amp;quot;user == user1: &amp;quot;&lt;/span&gt; + (user == user1)); &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    sqlSession.close();  &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    sqlSession2.close();  &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;// User&amp;#123;id=1, name=&amp;#x27;haha&amp;#x27;&amp;#125;&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;// User&amp;#123;id=1, name=&amp;#x27;haha&amp;#x27;&amp;#125;&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;// user == user1: true&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;/p&gt;
